msg.payload = {
    deviceId: "691251b02900a86edfa65db7",
    fieldId: "35124a0b-3430-4764-9868-009d74821f6e",
    value: value,
    status: value === 1 ? "Dry" : "Wet",
    timestamp: new Date().toLocaleString()
};
return msg;

// Extract real sensor data
var temperature = parseFloat(msg.payload);
var humidity = parseFloat(msg.humidity);

// Generate a normally distributed value using Boxâ€“Muller
function randNormal(mean, stdDev) {
    let u = 0, v = 0;
    while (u === 0) u = Math.random(); // Avoid log(0)
    while (v === 0) v = Math.random();
    let num = Math.sqrt(-2.0 * Math.log(u)) * Math.cos(2.0 * Math.PI * v);
    return mean + num * stdDev;
}

// Clamp values to realistic bounds
function clamp(value, min, max) {
    return Math.max(min, Math.min(max, value));
}

// Simulated values with natural distribution
var soilMoisture = clamp(randNormal(45, 10), 10, 100);    // avg 45%, sd 10
var nitrogen = clamp(randNormal(40, 12), 10, 100);    // mg/kg
var phosphorus = clamp(randNormal(20, 6), 5, 100);     // mg/kg
var potassium = clamp(randNormal(100, 30), 20, 200);   // mg/kg
var pH = clamp(randNormal(6.5, 0.4), 5.5, 8);   // pH
var rainfall = clamp(randNormal(5, 7), 0, 30);        // mm

// Determine statuses
var tempStatus;
if (temperature < 10) tempStatus = "Very Cold";
else if (temperature < 18) tempStatus = "Cold";
else if (temperature < 25) tempStatus = "Normal";
else if (temperature < 30) tempStatus = "Warm";
else tempStatus = "Hot";

var soilStatus;
if (soilMoisture < 20) soilStatus = "Very Dry";
else if (soilMoisture < 40) soilStatus = "Dry";
else if (soilMoisture < 60) soilStatus = "Normal";
else if (soilMoisture < 80) soilStatus = "Humid";
else soilStatus = "Very Humid";

// Final combined payload
msg.payload = {
    deviceId: "b924c3eb-62b8-4664-aa7f-f51f72d8130c",
    fieldId: "35124a0b-3430-4764-9868-009d74821f6e",
    temperature: parseFloat(temperature.toFixed(2)),
    humidity: parseFloat(humidity.toFixed(2)),      // air humidity
    soil_moisture: parseFloat(soilMoisture.toFixed(2)),
    nitrogen: parseFloat(nitrogen.toFixed(2)),
    phosphorus: parseFloat(phosphorus.toFixed(2)),
    potassium: parseFloat(potassium.toFixed(2)),
    pH: parseFloat(pH.toFixed(2)),
    rainfall: parseFloat(rainfall.toFixed(2)),
    soilType: "Loamy",
    cropType: "rice",
    temperature_status: tempStatus,
    soil_moisture_status: soilStatus,
    timestamp: new Date().toISOString()
};

return msg;
